import React from 'react';
import CreateList from './CreateList'
import './Todo.css'
class App extends React.Component{
    constructor(props){
        super(props);
        this.state={
            items:[],
            currentItem:{
                text:'',
                key:''
            }
        }
        this.handleInput=this.handleInput.bind(this);
    }
    handleInput(e){
        this.setState({
            currentItem:{
                text:e.target.value,
                key:Date.now()
            }
        })
    }
    render(){
        return(
            <div className="App">
            <header>
                <form id="to-do-app" onSubmit={this.addItem}>
                 <div>
                    <h2>All Tasks</h2>
                    <input  id="input" type="text" placeholder="Add a new task"
                    value={this.state.currentItem.text}
                    onChange={this.handleInput}/>
                    <button onclick="addItem()" class="addBtn">Add</button>
                </div>
            <div class="todo">
               <ul id="mytodolt">
               </ul>
            </div>
            <div id="para">
                <p>Seems like a quiet day</p>
            </div>
            </form>
            </header>
            </div> 
        )
    }
}
export default App;
