import React from 'react';
import './Todo.css'
import Listitems from './Listitems';
// import{library} from '@fortawesome/fontawesome-svg-core';
// import{faTrash} from '@fortawesome/free-solid-svg-icons';

// library.add(faTrash);

class App extends React.Component{
    constructor(props){
        super(props);
        this.state={
            items:[],
            currentItem:{
                text:'',
                key:''
            }
        }
        this.handleInput=this.handleInput.bind(this);
        this.addItem=this.addItem.bind(this);
        this.deleteItem=this.deleteItem.bind(this);
    }
    handleInput(e){
        this.setState({
            currentItem:{
                text:e.target.value,
                key:Date.now()
            }
        })
    }
    addItem(e){
        e.preventDefault();
        const newItem=this.state.currentItem;
        console.log(newItem);
        if(newItem.text!==""){
            const newItems=[...this.state.items,newItem];
            this.setState({
                items:newItems,
                currentItem:{
                    text:'',
                    key:''
                }
            })

        }
    }
    deleteItem(key){
        const filteredItems= this.state.items.filter(item=>
            item!==key);
            this.setState({
                items:filteredItems
            })
    }
    render(){
        return(
            <div className="App">
            <header>
                <form id="to-do-app" onSubmit={this.addItem}>
                <h2>All Tasks</h2>
                    <input  id="input" type="text" placeholder="Add a new task"
                    value={this.state.currentItem.text}
                    onChange={this.handleInput}/>
                    <button type="submit">Add</button>


                    <div id="para">
        <p ><strong>Seems like  a quiet day</strong></p>
    </div>
                    {/* <div>
           {items.length === 0 && <h2 id="h2">Seems like a quiet day</h2>}
        </div> */}
            </form>
            </header>
            <Listitems items={this.state.items} deleteItem={this.deleteItem}></Listitems>
            </div> 
        );
    }
}
export default App;
